"use strict";(self.webpackChunkalaaconference=self.webpackChunkalaaconference||[]).push([[1787],{8216:(e,n,t)=>{t.r(n),t.d(n,{assets:()=>c,contentTitle:()=>l,default:()=>p,frontMatter:()=>o,metadata:()=>a,toc:()=>d});const a=JSON.parse('{"id":"zabstracts/2/A2","title":"2.A2","description":"Mark Ferraretto, Senior Lecturer, Professor Tania Leiman, Dean of Law, Dr James Scheibner, Lecturer, Flinders University","source":"@site/docs/zabstracts/2/2.A2.mdx","sourceDirName":"zabstracts/2","slug":"/zabstracts/2/A2","permalink":"/2024ALAAConference/docs/zabstracts/2/A2","draft":false,"unlisted":false,"tags":[],"version":"current","sidebarPosition":2,"frontMatter":{"title":"2.A2","hide_title":true},"sidebar":"tutorialSidebar","previous":{"title":"2.A1","permalink":"/2024ALAAConference/docs/zabstracts/2/A1"},"next":{"title":"2.A3","permalink":"/2024ALAAConference/docs/zabstracts/2/A3"}}');var s=t(4848),i=t(8453),r=t(9437);const o={title:"2.A2",hide_title:!0},l=void 0,c={},d=[{value:"Abstract",id:"abstract",level:2}];function h(e){const n={h2:"h2",p:"p",...(0,i.R)(),...e.components};return(0,s.jsxs)(s.Fragment,{children:[(0,s.jsx)(r.y,{sKey:"id2_A2",children:(0,s.jsx)(n.p,{children:"Mark Ferraretto, Senior Lecturer, Professor Tania Leiman, Dean of Law, Dr James Scheibner, Lecturer, Flinders University"})}),"\n",(0,s.jsx)(n.h2,{id:"abstract",children:"Abstract"}),"\n",(0,s.jsx)(n.p,{children:"Technology has had an outsize impact on the legal profession, fundamentally transforming legal\nresearch, legal practice and, to a lesser degree, the provision of legal services. We are now on the\ncusp of potentially another fundamental paradigm shift as generative AI systems continue to\nmature. Consequently, it is increasingly crucial that law students and professionals understand legal\ntechnology, its potential and its challenges."}),"\n",(0,s.jsx)(n.p,{children:"At Flinders University, our approach has been to expose students to fundamental technology\nconcepts. These concepts include an understanding of decision-making logic, the nature of data and\nits storage and manipulation. We do this by teaching students how to express legal issues as\nsoftware applications."}),"\n",(0,s.jsx)(n.p,{children:"We started on our journey in 2019 using a proprietary technology platform and focussed on building\nsimple legal applications for not-for-profit organisations (NFPs). However, accessibility and\naffordability issues led us to shift in 2020 to an open source platform. This shift required creating\nteaching materials from scratch. This shift also facilitated interdisciplinary collaborations with\nComputer Science staff and students, and with colleagues from Nursing and Health Sciences. Since\nthese topics commenced our students have created approximately 70 legal software applications for\nover 40 NFPs in our state, across Australia and even internationally. Skills developed by students\nhave opened new opportunities and employment pathways locally, nationally and internationally."}),"\n",(0,s.jsx)(n.p,{children:"Despite this success, and despite continued encouragement from the profession, students find it\nhard to see how what they are learning connects with the law and with what lawyers do."}),"\n",(0,s.jsx)(n.p,{children:"This presentation will report on our experiences over the past 5 years in developing this innovative\ncurriculum, including a self-published online textbook. This presentation will also explore how we\nare responding to feedback from students and NFP clients, as well as accelerating technological\nchange."})]})}function p(e={}){const{wrapper:n}={...(0,i.R)(),...e.components};return n?(0,s.jsx)(n,{...e,children:(0,s.jsx)(h,{...e})}):h(e)}}}]);