"use strict";(self.webpackChunkalaaconference=self.webpackChunkalaaconference||[]).push([[2623],{582:(e,t,n)=>{n.r(t),n.d(t,{assets:()=>l,contentTitle:()=>c,default:()=>p,frontMatter:()=>o,metadata:()=>a,toc:()=>d});const a=JSON.parse('{"id":"zabstracts/1/A1","title":"1.A1","description":"Rob Chalmers, Senior Lecturer, Flinders University","source":"@site/docs/zabstracts/1/1.A1.mdx","sourceDirName":"zabstracts/1","slug":"/zabstracts/1/A1","permalink":"/2024ALAAConference/docs/zabstracts/1/A1","draft":false,"unlisted":false,"tags":[],"version":"current","sidebarPosition":1,"frontMatter":{"title":"1.A1","hide_title":true},"sidebar":"tutorialSidebar","previous":{"title":"Concurrent Session 1","permalink":"/2024ALAAConference/docs/zabstracts/1/"},"next":{"title":"1.A2","permalink":"/2024ALAAConference/docs/zabstracts/1/A2"}}');var i=n(4848),s=n(8453),r=n(9437);const o={title:"1.A1",hide_title:!0},c=void 0,l={},d=[{value:"Abstract",id:"abstract",level:2}];function h(e){const t={h2:"h2",p:"p",...(0,s.R)(),...e.components};return(0,i.jsxs)(i.Fragment,{children:[(0,i.jsx)(r.y,{sKey:"id1_A1",children:(0,i.jsx)(t.p,{children:"Rob Chalmers, Senior Lecturer, Flinders University"})}),"\n",(0,i.jsx)(t.h2,{id:"abstract",children:"Abstract"}),"\n",(0,i.jsx)(t.p,{children:"This presentation is inspired by Neal Stephenson\u2019s 1995 speculative fiction \u2018The\nDiamond Age\u2019. That novel included a portrayal of a sophisticated and responsive\ninteractive tutor (Young Lady's Illustrated Primer) which combined an AI system with\na human actor. The tutor was initially developed for a privileged upper class, then\nillicitly made more broadly available."}),"\n",(0,i.jsx)(t.p,{children:"What was fiction is now fact, with traces of these ideas visible in current uses of AI in\neducation. These include the use of AI to \u201creskin\u201d full motion video to fit the avatars\nof different people (or even render video from text only inputs). Generative AI tools\nallow the creation of seemingly naturalistic responses from artificial tutors, and can\nbe readily reprogrammed to play different roles in different fields with simple\nnarrative prompts (akin to the suggestions that might be made to actors\nworkshopping a play). AI is now integrated into commonplace office, productivity and\nsearch software, including law specific search and productivity tools."}),"\n",(0,i.jsx)(t.p,{children:"What are the implications for educational access and quality, academic integrity, and\nvalues? What about the rights of those creating educational tools and materials \u2013\nincluding rights to the future use of their likenesses and moral as well as economic\nrights in their creations (imagine an educational version of the dispute between the\nWriters Guild of America and the Alliance of Motion Picture and Television\nProducers)? What are the consequences for professional standards and practices?\xa0\nFinally, might we actually find effective responses to some of these challenges in\nolder approaches to education and training?"}),"\n",(0,i.jsx)(t.p,{children:"[This presentation will draw on recent examples of approaches in the use of AI in\nother educational fields; examples of use in legal practice; and on recent and\npending presentations and panel discussions of which the presenter has been a part\n(for the Australasian Legal Practice Management Association and for a European\nconference on AI)]"})]})}function p(e={}){const{wrapper:t}={...(0,s.R)(),...e.components};return t?(0,i.jsx)(t,{...e,children:(0,i.jsx)(h,{...e})}):h(e)}}}]);